svg.script:
let road_path_1 = document.getElementById("train_path_1");
let road_path_2 = document.getElementById("train_path_2");
let road_path_3 = document.getElementById("train_path_3");
let road_path_4 = document.getElementById("train_path_4");
let train_1_1 = document.getElementById("train_1_1");
let train_1_2 = document.getElementById("train_1_2");
let train_1_3 = document.getElementById("train_1_3");
let train_2_1 = document.getElementById("train_2_1");
let train_2_2 = document.getElementById("train_2_2");
let train_2_3 = document.getElementById("train_2_3");
let train_3_1 = document.getElementById("train_3_1");
let train_3_2 = document.getElementById("train_3_2");
let train_3_3 = document.getElementById("train_3_3");
let train_4_1 = document.getElementById("train_4_1");
let train_4_2 = document.getElementById("train_4_2");
let train_4_3 = document.getElementById("train_4_3");
let t = 0;
let num = 0;
function road_mod(t) {
	return (t + road_path_1.getTotalLength()) % road_path_1.getTotalLength();
}
function sleep_ms(ms) {
	return new Promise(resolve => setTimeout(resolve, ms));
}
async function next_step() {
	num = num+1;
	t = t + 0.4;
	t = t % road_path_1.getTotalLength();
	console.log(num, t);
	let tr_1_1_f = road_path_1.getPointAtLength(road_mod(t+4+9));
	let tr_1_1_b = road_path_1.getPointAtLength(road_mod(t-4+9));
	let tr_1_2_f = road_path_1.getPointAtLength(road_mod(t+3));
	let tr_1_2_b = road_path_1.getPointAtLength(road_mod(t-3));
	let tr_1_3_f = road_path_1.getPointAtLength(road_mod(t+3-8));
	let tr_1_3_b = road_path_1.getPointAtLength(road_mod(t-3-8));
	let tr_2_1_f = road_path_2.getPointAtLength(road_mod(t+4+9));
	let tr_2_1_b = road_path_2.getPointAtLength(road_mod(t-4+9));
	let tr_2_2_f = road_path_2.getPointAtLength(road_mod(t+3));
	let tr_2_2_b = road_path_2.getPointAtLength(road_mod(t-3));
	let tr_2_3_f = road_path_2.getPointAtLength(road_mod(t+3-8));
	let tr_2_3_b = road_path_2.getPointAtLength(road_mod(t-3-8));
	let tr_3_1_f = road_path_3.getPointAtLength(road_mod(t+4+9));
	let tr_3_1_b = road_path_3.getPointAtLength(road_mod(t-4+9));
	let tr_3_2_f = road_path_3.getPointAtLength(road_mod(t+3));
	let tr_3_2_b = road_path_3.getPointAtLength(road_mod(t-3));
	let tr_3_3_f = road_path_3.getPointAtLength(road_mod(t+3-8));
	let tr_3_3_b = road_path_3.getPointAtLength(road_mod(t-3-8));
	let tr_4_1_f = road_path_4.getPointAtLength(road_mod(t+4+9));
	let tr_4_1_b = road_path_4.getPointAtLength(road_mod(t-4+9));
	let tr_4_2_f = road_path_4.getPointAtLength(road_mod(t+3));
	let tr_4_2_b = road_path_4.getPointAtLength(road_mod(t-3));
	let tr_4_3_f = road_path_4.getPointAtLength(road_mod(t+3-8));
	let tr_4_3_b = road_path_4.getPointAtLength(road_mod(t-3-8));

	train_1_1.setAttribute("transform", `translate(${(tr_1_1_f.x + tr_1_1_b.x)/2} ${(tr_1_1_f.y + tr_1_1_b.y)/2}) rotate(${Math.atan2(tr_1_1_f.x - tr_1_1_b.x, tr_1_1_b.y - tr_1_1_f.y)*180/Math.PI})`);
	train_1_2.setAttribute("transform", `translate(${(tr_1_2_f.x + tr_1_2_b.x)/2} ${(tr_1_2_f.y + tr_1_2_b.y)/2}) rotate(${Math.atan2(tr_1_2_f.x - tr_1_2_b.x, tr_1_2_b.y - tr_1_2_f.y)*180/Math.PI})`);
	train_1_3.setAttribute("transform", `translate(${(tr_1_3_f.x + tr_1_3_b.x)/2} ${(tr_1_3_f.y + tr_1_3_b.y)/2}) rotate(${Math.atan2(tr_1_3_f.x - tr_1_3_b.x, tr_1_3_b.y - tr_1_3_f.y)*180/Math.PI})`);
	train_2_1.setAttribute("transform", `translate(${(tr_2_1_f.x + tr_2_1_b.x)/2} ${(tr_2_1_f.y + tr_2_1_b.y)/2}) rotate(${Math.atan2(tr_2_1_f.x - tr_2_1_b.x, tr_2_1_b.y - tr_2_1_f.y)*180/Math.PI})`);
	train_2_2.setAttribute("transform", `translate(${(tr_2_2_f.x + tr_2_2_b.x)/2} ${(tr_2_2_f.y + tr_2_2_b.y)/2}) rotate(${Math.atan2(tr_2_2_f.x - tr_2_2_b.x, tr_2_2_b.y - tr_2_2_f.y)*180/Math.PI})`);
	train_2_3.setAttribute("transform", `translate(${(tr_2_3_f.x + tr_2_3_b.x)/2} ${(tr_2_3_f.y + tr_2_3_b.y)/2}) rotate(${Math.atan2(tr_2_3_f.x - tr_2_3_b.x, tr_2_3_b.y - tr_2_3_f.y)*180/Math.PI})`);
	train_3_1.setAttribute("transform", `translate(${(tr_3_1_f.x + tr_3_1_b.x)/2} ${(tr_3_1_f.y + tr_3_1_b.y)/2}) rotate(${Math.atan2(tr_3_1_f.x - tr_3_1_b.x, tr_3_1_b.y - tr_3_1_f.y)*180/Math.PI})`);
	train_3_2.setAttribute("transform", `translate(${(tr_3_2_f.x + tr_3_2_b.x)/2} ${(tr_3_2_f.y + tr_3_2_b.y)/2}) rotate(${Math.atan2(tr_3_2_f.x - tr_3_2_b.x, tr_3_2_b.y - tr_3_2_f.y)*180/Math.PI})`);
	train_3_3.setAttribute("transform", `translate(${(tr_3_3_f.x + tr_3_3_b.x)/2} ${(tr_3_3_f.y + tr_3_3_b.y)/2}) rotate(${Math.atan2(tr_3_3_f.x - tr_3_3_b.x, tr_3_3_b.y - tr_3_3_f.y)*180/Math.PI})`);
	train_4_1.setAttribute("transform", `translate(${(tr_4_1_f.x + tr_4_1_b.x)/2} ${(tr_4_1_f.y + tr_4_1_b.y)/2}) rotate(${Math.atan2(tr_4_1_f.x - tr_4_1_b.x, tr_4_1_b.y - tr_4_1_f.y)*180/Math.PI})`);
	train_4_2.setAttribute("transform", `translate(${(tr_4_2_f.x + tr_4_2_b.x)/2} ${(tr_4_2_f.y + tr_4_2_b.y)/2}) rotate(${Math.atan2(tr_4_2_f.x - tr_4_2_b.x, tr_4_2_b.y - tr_4_2_f.y)*180/Math.PI})`);
	train_4_3.setAttribute("transform", `translate(${(tr_4_3_f.x + tr_4_3_b.x)/2} ${(tr_4_3_f.y + tr_4_3_b.y)/2}) rotate(${Math.atan2(tr_4_3_f.x - tr_4_3_b.x, tr_4_3_b.y - tr_4_3_f.y)*180/Math.PI})`);
	await sleep_ms(10);
}
async function start() {
	while (true) {
		await next_step();
	}
}
start();
